import { useQuery } from "react-query";

const getRequest = async (_, { endpoint }) => {
  const response = await fetch(endpoint);
  return response.json();
};

export function useShirts() {
  // @ts-ignore
  return useQuery(["shirts", { endpoint: "/api/getShirts" }], getRequest);
}
export function useShirt(id) {
  return useQuery(
    ["shirt", { endpoint: `/api/getShirts?id=${id}` }],
    // @ts-ignore
    getRequest
  );
}
