import { useQuery } from "react-query";

const getRequest = async (_, { endpoint }) => {
  const response = await fetch(endpoint);
  return response.json();
};

export function useShirts() {
  return useQuery(
    ["/api/getShirts", { endpoint: "/api/getShirts" }],
    getRequest
  );
}
export function useShirt(id) {
  return useQuery(
    [`/api/getShirt?id=${id}`, { endpoint: "/api/getShirts" }],
    getRequest
  );
}
